mapper.select:
select *
from orderinfo
where order_no = #{order_no}
  and order_passwd = #{order_passwd}

select *
from orderinfo
where order_passwd = #{order_passwd}
order by order_no desc limit 1;

select order_bookno, order_booktype, order_scount, order_sum
from orderinfo
where orderlist_no = #{orderlist_no}

select orderlist_no, order_person, date (order_date) order_date, order_paytype, order_state, sum (order_sum) order_sum, order_address
from orderinfo
where orderlist_no = #{orderlist_no}
group by orderlist_no;

mapper.update:
